import styled from "styled-components";
import { Link } from "react-router-dom";

export const LarryContainer = styled.div`
  height: auto;
  background: rgb(255, 255, 255);

  @media screen and (max-width: 960px) {
    height: 100vh;
  }
`;

export const LarryWrapper = styled.div`
  position: relative;
  margin: 0 auto;
  max-width: 1200px;
  padding: 10% 0;
`;

export const Icon = styled(Link)`
  background: linear-gradient(
    329deg,
    rgba(68, 68, 68, 1) 60%,
    rgba(47, 48, 48, 1) 85%,
    rgba(1, 6, 6, 1) 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  text-decoration: none;
  position: absolute;
  right: 0;
  font-weight: 700;
  font-size: 24px;
`;

export const LarryColumn = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(250px, 1fr);
  grid-column-gap: 10px;
  grid-row-gap: 10px;
  align-items: left;
  max-width: 900px;
  margin: 0 auto;
`;

export const Header = styled.div`
  grid-area: 1 / 1 / 2 / 2;
  background: #fc9;
`;

export const LarryH1 = styled.h1``;

export const LarryH2 = styled.h2``;

export const LarryPRight = styled.p`
  grid-area: 2 / 1 / 3 / 2;
`;

export const LarryPLeft = styled.p`
  grid-area: 2 / 1 / 3 / 2;
`;
