import styled from "styled-components";
import { Link } from 'react-router-dom';

export const LarryContainer = styled.div`
  height: 800px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  text-align: center;
  background: rgb(219, 237, 242);

  @media screen and (max-width: 768px) {
    height: 1100px;
  }

  @media screen and (max-width: 480px) {
    height: 1300px;
  }
`;

export const LarryIcon = styled(Link)`
  margin-left: 32px;
  margin-top: 32px;
  text-decoration: none;
  color: #444;
  font-weight: 700;
  font-size: 32px;
`;

export const LarryWrapper = styled.div`
  max-width: 1000px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
  align-items: center;
  grid-gap: 16px;
  padding: 0 50px;

  @media screen and (max-width: 768px) {
    grid-template-columns: 1fr;
    padding: 0 20px;
  }
`;

export const LarryH1 = styled.h1`
  font-size: 2.5rem;
  color: #fff;
  margin-bottom: 64px;
`;

export const LarryH2 = styled.h2`
  font-size: 1rem;
  margin-bottom: 10px;
`;

export const LarryP = styled.p`
  font-size: 1rem;
  text-align: center;
`;
