    <Router>
      <Switch>
        <Route path="/" component={Home} exact />
        <Route path="/larryPage" component={larryPage} exact />
        <Route path="/vectorPage" component={vectorPage} exact />
        <Route path="/darumaPage" component={darumaPage} exact />
      </Switch>
    </Router>
  );
}

export default App;
