import styled from "styled-components";
import { Link } from "react-router-dom";

export const DarumaContainer = styled.div`
  height: auto;
  background: rgb(255, 255, 255);

  @media screen and (max-width: 960px) {
    height: 100vh;
  }
`;

export const DarumaWrapper = styled.div`
  position: relative;
  margin: 0 auto;
  max-width: 1200px;
  padding: 10% 0;

  @media screen and (max-width: 970px) {
    max-width: 80%;
    padding: 15% 0;
  }

  @media screen and (max-width: 520px) {
    max-width: 85%;
    padding: 20% 0;
  }
`;

export const IconWrapper = styled.div`
  position: relative;
  z-index: 1;
`;

export const Icon = styled(Link)`
  display: block;
  background: linear-gradient(
    329deg,
    rgba(68, 68, 68, 1) 60%,
    rgba(47, 48, 48, 1) 85%,
    rgba(1, 6, 6, 1) 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  max-width: 900px;
  margin: 0 auto;
  padding: 2rem 1rem;
  font-size: 20px;
  font-weight: 700;
  position: absolute;
  top: 3rem;
  right: 3rem;
  transition: all 1s linear 0s;
  cursor: pointer;

  &:hover {
    transform: rotateZ(360deg);
  }

  @media screen and (max-width: 970px) {
    padding: 1rem 0.5rem;
    position: absolute;
    top: 2rem;
    right: 2rem;
  }

  @media screen and (max-width: 520px) {
    font-size: 17px;
    position: absolute;
    top: 1rem;
    right: 1rem;
  }
`;

export const DarumaTitle = styled.div`
  background: linear-gradient(
    329deg,
    rgba(68, 68, 68, 1) 60%,
    rgba(47, 48, 48, 1) 85%,
    rgba(1, 6, 6, 1) 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  max-width: 900px;
  margin: 0 auto;
  font-weight: 900;
  font-size: 66px;
  margin-bottom: 1rem;
`;

export const DarumaColumn = styled.div`
  display: grid;
  grid-template-columns: 250px 1fr;
  gap: 1rem;
  align-items: left;
  max-width: 900px;
  margin: 0 auto;

  @media screen and (max-width: 970px) {
    grid-template-columns: 30% 1fr;
    gap: 0.7rem;
    align-items: left;
    max-width: 85%;
  }

  @media screen and (max-width: 520px) {
    grid-template-columns: 35% 1fr;
    gap: 0.5rem;
    max-width: 90%;
  }
`;

export const DarumaH1 = styled.h1`
  color: #010606;
  max-width: 900px;
  margin: 0 auto;
  font-weight: 300;
  font-size: 20px;
  letter-spacing: 1.4px;
  margin-bottom: 5rem;

  @media screen and (max-width: 520px) {
    margin-bottom: 3.5rem;
  }
`;

export const DarumaH2 = styled.h2``;

export const DarumaP = styled.p`
  color: #888888;
  font-size: 14px;
  font-weight: 300;
  line-height: 16px;
  letter-spacing: 1.4px;
`;
