import { React } from "react";
import { Button } from "../ButtonElements";

import { AnimatePresence, motion } from "framer-motion";
import { useHistory } from "react-router";
import {
  Link,
  Route,
  useLocation,
  Switch,
  BrowserRouter,
} from "react-router-dom";

import {
  InfoContainer,
  InfoWrapper,
  InfoRow,
  Column1,
  Column2,
  TextWrapper,
  TopLine,
  Heading,
  Subtitle,
  BtnWrap,
  ImgWrap,
  Img,
  ArrowForward,
  ArrowRight,
} from "./InfoElements";

const InfoSection = ({
  id,
  to,
  lightBg,
  lightText,
  darkText,
  topLine,
  headline,
  description,
  buttonLabel,
  imgStart,
  img,
  alt,
  dark,
  primary,
}) => {
  return (
    <InfoContainer lightBg={lightBg} id={id}>
      <motion.div
    animate={{
      x: 0,
      opacity: 1
    }}
    initial={{
      x: 100,
      opacity: 0
    }}
    exit={{
      x: -100,
      opacity: 0
    }}
    transition={{
      duration: 0.2
    }}
  ></motion.div>
        <InfoWrapper>
          <InfoRow imgStart={imgStart}>
            <Column1>
              <TextWrapper>
                <TopLine>{topLine}</TopLine>
                <Heading lightText={lightText}>{headline}</Heading>
                <Subtitle darkText={darkText}>{description}</Subtitle>
                <BtnWrap>
                  <Button
                    to={to}
                    smooth={true}
                    duration={500}
                    spy={true}
                    exact="true"
                    offset={-80}
                    primary={primary ? 1 : 0}
                    dark={dark ? 1 : 0}
                  >
                    {buttonLabel} <ArrowRight />
                  </Button>
                </BtnWrap>
              </TextWrapper>
            </Column1>
            <Column2>
              <ImgWrap>
                <Img src={img} alt={alt} />
              </ImgWrap>
            </Column2>
          </InfoRow>
        </InfoWrapper>
      </InfoContainer>
  );
};

export default InfoSection;
