import React, { useState } from "react";
import * as FadeIn from "../FadeIn";
import { motion } from "framer-motion";
import {
  TitleContainer,
  TitleContent,
  ImageContent,
  H1Content,
  TitleH1,
  PContent,
  TitleP,
  Image,
} from "./TitleElements";
import HeadShot2 from "../../images/HeadShot2.png";

const TitleSection = () => {
  const fadeRight = {
    hidden: { opacity: 0, x: 200 },
    visible: { opacity: 1, x: 0 },
  };

  return (
    <TitleContainer>
      <TitleContent>
        <ImageContent>
        <motion.image
            variants={fadeRight}
            initial="hidden"
            animate="visible"
            transition={{ duration: 1 }}
          >
            <Image src={HeadShot2} id="about" />
</motion.image>
        </ImageContent>
        <H1Content>
          <FadeIn.Right>
            <TitleH1>
              I am Yuya Hashirizaki
              <FadeIn.Right>
                <TitleH1 className="br">
                  design & build digital products based in Vancouver
                </TitleH1>
              </FadeIn.Right>
            </TitleH1>
          </FadeIn.Right>
        </H1Content>
        <PContent>
          <FadeIn.Right>
            <TitleP>
              Iâ€™m a passionate multi-talented human who comes from Japan with
              over 6+ years of experiences in wider field of design disciplines.
            </TitleP>
          </FadeIn.Right>
        </PContent>
      </TitleContent>
    </TitleContainer>
  );
};

export default TitleSection;
