import { React } from "react";
import { Button } from "../ButtonElements";
import { motion } from "framer-motion";
import {
  InfoContainer,
  InfoWrapper,
  InfoRow,
  Column1,
  Column2,
  TextWrapper,
  TextWrapperR,
  TopLine,
  TopLineR,
  Heading,
  Heading2,
  TextR,
  Subtitle,
  BtnWrap,
  ArrowForward,
  ArrowRight,
} from "./InfoElements";

const InfoSection = ({
  id,
  to,
  lightBg,
  lightText,
  darkText,
  topLine,
  headline,
  headline2,
  description,
  buttonLabel,
  textR,
  dark,
  btnColor,
  primary,
}) => {
  return (
    <InfoContainer lightBg={lightBg} id={id}>
      <motion.div
        animate={{
          x: 0,
          opacity: 1,
        }}
        initial={{
          x: 700,
          opacity: 0,
        }}
        exit={{
          x: -700,
          opacity: 0,
        }}
        transition={{
          duration: 0.5,
        }}
      >
        <InfoWrapper>
          <InfoRow>
            <ColumnWrapper></ColumnWrapper>
            <Column1>
              <TextWrapper>
                <TopLine>{topLine}</TopLine>
                <Heading lightText={lightText}>{headline}</Heading>
                <Heading2 lightText={lightText}>{headline2}</Heading2>
                <Subtitle darkText={darkText}>{description}</Subtitle>
                <BtnWrap primary={primary ? 1 : 0}>
                  <Button
                    to={to}
                    smooth={true}
                    duration={500}
                    spy={true}
                    exact="true"
                    offset={-80}
                    btnColor={btnColor ? 1 : 0}
                    dark={dark ? 1 : 0}
                  >
                    {buttonLabel} <ArrowRight />
                  </Button>
                </BtnWrap>
              </TextWrapper>
            </Column1>
            <Column2>
              <TextWrapperR>
                <TopLineR>Data</TopLineR>
                <TextR lightText={lightText}>{textR}</TextR>
              </TextWrapperR>
            </Column2>
          </InfoRow>
        </InfoWrapper>
      </motion.div>
    </InfoContainer>
  );
};

export default InfoSection;
